import { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Circle } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

// Default icon fix
delete (L.Icon.Default.prototype as any)._getIconUrl;
L.Icon.Default.mergeOptions({
  iconUrl: "https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png",
  shadowUrl: "https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png",
});

interface Station {
  id: number;
  name: string;
  latitude: number;
  longitude: number;
}

interface ParkingZone {
  id: number;
  name: string;
  latitude: number;
  longitude: number;
  radius: number;
}

interface Scooter {
  id: number;
  serial_number: string;
  current_latitude: number;
  current_longitude: number;
  status: string;
}

const Map = () => {
  const [stations, setStations] = useState<Station[]>([]);
  const [zones, setZones] = useState<ParkingZone[]>([]);
  const [scooters, setScooters] = useState<Scooter[]>([]);

  useEffect(() => {
    const fetchData = async () => {
      const headers = {
        Authorization: `Bearer ${localStorage.getItem("jwt")}`,
      };

      app.use("/v1/cities", citiesRoutes); // All city-related routes start with /v1/cities
      app.use("/v1/parking", parkingsRoutes); // All parking-related routes start with /v1/parking
      app.use("/v1/user", userRoutes); // All user-related routes start with /v1/user
      app.use("/v1/travels", travelsRoutes); // All travels-related routes start with /v1/travels
      app.use("/v1/chargingstations", chargingStationsRoutes); // All chargingstations-related routes start with /v1/chargingstations
      app.use("/v1/bikes", bikesRoutes); // All bikes-related routes start with /v1/bikes
      app.use("/v1/payment", paymentsRoutes);

      try {
        const [stationRes, zoneRes, scooterRes] = await Promise.all([
          fetch("http://localhost:5000/v1/chargingstations", { headers }),
          fetch("http://localhost:5000/v1/parkings"),
          fetch("http://localhost:5000/v1/scooters"),
        ]);

        const stationsData = await stationRes.json();
        const zonesData = await zoneRes.json();
        const scootersData = await scooterRes.json();

        setStations(stationsData.stations);
        setZones(zonesData.parkings);
        setScooters(scootersData.scooters);
      } catch (err) {
        console.error("Error fetching data", err);
      }
    };

    fetchData();
  }, []);

  return (
    <MapContainer center={[59.3293, 18.0686]} zoom={12} style={{ height: "90vh", width: "100%" }}>
      <TileLayer
        attribution='&copy; OpenStreetMap'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />

      {/* Charging Stations */}
      {stations.map((station) => (
        <Marker key={`station-${station.id}`} position={[station.latitude, station.longitude]}>
          <Popup>
            <strong>Laddstation</strong><br />
            {station.name}
          </Popup>
        </Marker>
      ))}

      {zones.map((zone) => (
        <Circle
          key={`zone-${zone.id}`}
          center={[zone.latitude, zone.longitude]}
          radius={zone.radius}
          pathOptions={{ color: "blue" }}
        >
          <Popup>
            <strong>Parkering</strong><br />
            {zone.name}
          </Popup>
        </Circle>
      ))}

      {/* Scooters */}
      {scooters.map((scooter) => (
        <Marker
          key={`scooter-${scooter.id}`}
          position={[scooter.current_latitude, scooter.current_longitude]}
        >
          <Popup>
            <strong>Scooter</strong><br />
            Serial: {scooter.serial_number}<br />
            Status: {scooter.status}
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default Map;
