import { useEffect, useState } from "react";
import auth from "../modules/auth";

interface UserInfo {
  name: string;
  email: string;
  balance: number;
}

const Dashboard = () => {
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(true);
  const [user, setUser] = useState<UserInfo | null>(null);

  useEffect(() => {
    // Check for token
    if (!auth.token) {
      setIsLoggedIn(false);
      return;
    }

    // If you have user data in auth, use it here!
    // Replace this with a fetch call if needed.
    if (auth.user) {
      setUser({
        name: auth.user.name,
        email: auth.user.email,
        balance: auth.user.balance ?? 0,
      });
    } else {
      // fallback (if you need to fetch user, do it here)
      setUser({
        name: "Demo User",
        email: "demo@user.com",
        balance: 150.0,
      });
    }
  }, []);

  if (!isLoggedIn) {
    return (
      <div className="customer-dashboard-card">
        <h2 className="customer-dashboard-title">Not logged in</h2>
        <div className="customer-dashboard-info">
          <div>Please log in to view your account.</div>
        </div>
      </div>
    );
  }

  return (
    <div className="customer-dashboard-card">
      <h2 className="customer-dashboard-title">My Account</h2>
      <div className="customer-dashboard-info">
        <div>
          <span className="info-label">Name:</span> {user?.name ?? "-"}
        </div>
        <div>
          <span className="info-label">Email:</span> {user?.email ?? "-"}
        </div>
        <div>
          <span className="info-label">Balance:</span>{" "}
          {user ? `${user.balance.toFixed(2)} SEK` : "-"}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
