import { useEffect, useState } from "react";
import auth from "../modules/auth";

interface UserInfo {
  name: string;
  email: string;
  balance: number;
}

const Dashboard = () => {
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(true);
  const [user, setUser] = useState<UserInfo | null>(null);

  useEffect(() => {
    if (!auth.token) {
      setIsLoggedIn(false);
      return;
    }

    // Use your actual user fetching logic here
    if (auth.user) {
      setUser({
        name: auth.user.name,
        email: auth.user.email,
        balance: auth.user.balance ?? 0,
      });
    } else {
      setUser({
        name: "Demo User",
        email: "demo@user.com",
        balance: 150.0,
      });
    }
  }, []);

  return (
    <div className="customer-dashboard-main">
      <div className="customer-dashboard-card">
        <h2 className="customer-dashboard-title">
          {isLoggedIn ? "My Account" : "Not logged in"}
        </h2>
        <div className="customer-dashboard-info">
          {!isLoggedIn ? (
            <div>Please log in to view your account.</div>
          ) : (
            <>
              <div>
                <span className="info-label">Name:</span> {user?.name ?? "-"}
              </div>
              <div>
                <span className="info-label">Email:</span> {user?.email ?? "-"}
              </div>
              <div>
                <span className="info-label">Balance:</span>{" "}
                {user ? `${user.balance.toFixed(2)} SEK` : "-"}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
